import React, { useState } from "react";
import Typography from "@mui/material/Typography";
import { init, send } from "@emailjs/browser";
import { Grid, TextField, Button } from "@mui/material";

function Inquiry() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [content, setContent] = useState("");
  return (
    <div id="item_8" className="Inquiry" style={{ height: "35vh" }}>
      <form onSubmit={(e) => onSubmit(e)}>
        <Grid container flexDirection="column" textAlign="center" spacing={4}>
          <Grid item>
            <Typography variant="h4">お問い合わせ</Typography>
          </Grid>
          <Grid 
                        item
                        container
                        direction={"column"}
                        alignItems="flex-end"
                      >>
            <TextField
              id="name"
              label="お名前"
              variant="outlined"
              size="medium"
              onChange={(e) => setName(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              id="email"
              label="メールアドレス"
              variant="outlined"
              size="medium"
              onChange={(e) => setEmail(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              id="content"
              label="お問い合わせ内容"
              multiline
              variant="outlined"
              size="medium"
              onChange={(e) => setContent(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={6}>
            <Button type="submit" variant="contained" size="large">
              送信
            </Button>
          </Grid>
        </Grid>
      </form>
    </div>
  );
}
const onSubmit = async (props: any) => {
  // 必要なIDをそれぞれ環境変数から取得
  const userID = process.env.REACT_APP_PUBLICKEY || "";
  const serviceID = process.env.REACT_APP_SERVICE_ID || "";
  const templateID = process.env.REACT_APP_TEMPLATE_ID || "";
  // emailJS初期化
  console.log("aaa");
  init(userID);

  // emailJS送信データを定義
  const params = {
    from_name: props.name,
    email: props.email,
    content: props.content,
  };

  // emailJS送信
  try {
    await send(serviceID, templateID, params, userID);
    alert("送信成功");
  } catch (error) {
    // 送信失敗したらalertで表示
    alert(error);
  }
};
export default Inquiry;
