import React, { useState } from "react";
import Typography from "@mui/material/Typography";
import { init, send } from "@emailjs/browser";
import { Button, SendIcon } from "@mui/material";

function Inquiry() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [content, setContent] = useState("");
  return (
    <div id="item_8" className="Inquiry">
      <Typography variant="h4" style={{ textAlign: "center" }}>
        問い合わせ
        <br />
      </Typography>
      <div className="mx-auto mt-9 max-w-lg">
        <form
          onSubmit={(e) => onSubmit(e)}
          className="w-full space-y-9 rounded bg-white p-3  shadow-2xl"
        >
          <div>
            <label htmlFor="name" className=" mb-3 block font-bold text-black">
              お名前
            </label>
            <input
              id="name"
              type="text"
              placeholder="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="email" className=" mb-3 block font-bold text-black">
              メールアドレス
            </label>
            <input
              id="email"
              type="text"
              placeholder="your@examle.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div>
            <label
              htmlFor="content"
              className=" mb-3 block font-bold text-black"
            >
              お問い合わせ内容
            </label>
            <textarea
              id="content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </div>
          <Button
            type="submit"
            variant="contained"
            size="large"
            endIcon={<SendIcon />}
          >
            送信
          </Button>
        </form>
      </div>
    </div>
  );
}
const onSubmit = async (props: any) => {
  // 必要なIDをそれぞれ環境変数から取得
  const userID = process.env.REACT_APP_PUBLICKEY || "";
  const serviceID = process.env.REACT_APP_SERVICE_ID || "";
  const templateID = process.env.REACT_APP_TEMPLATE_ID || "";
  // emailJS初期化
  console.log("aaa");
  init(userID);

  // emailJS送信データを定義
  const params = {
    from_name: props.name,
    email: props.email,
    content: props.content,
  };

  // emailJS送信
  try {
    await send(serviceID, templateID, params, userID);
    alert("送信成功");
  } catch (error) {
    // 送信失敗したらalertで表示
    alert(error);
  }
};
export default Inquiry;
