import React, { useRef, useEffect } from "react";
import { Grid, ListItemText } from "@mui/material";
import ribon from "../dawnload/ribbon.mp4";
import RibonRight from "../assets/title/ribon_right.svg";
import RibonLeft from "../assets/title/ribon_left.svg";
import Vivus from "vivus";

function Title() {
  const videoRef = useRef<HTMLVideoElement>(null);
  useEffect(() => {
    // アニメーション
    new Vivus("ribonl", { duration: 300, file: RibonRight, type: "oneByOne" });
    new Vivus("ribonr", { duration: 300, file: RibonLeft, type: "oneByOne" });
  }, []);
  return (
    <div id="item_0" className="Title" style={{ height: "100vh" }}>
      <Grid
        container
        textAlign="center"
        alignItems="center"
        flexDirection={"column"}
      >
        <ListItemText
          primary="第12回日本言語聴覚士協会 九州地区学術集会 大分大会"
          primaryTypographyProps={{
            color: "#222222",
            variant: "h4",
            fontWeight: "bold",
            textAlign: "center",
          }}
          secondary="2022年1月29日〜30日"
          secondaryTypographyProps={{
            color: "red",
            variant: "h6",
            textAlign: "center",
          }}
        />
        <ListItemText
          primary="つむぐ"
          primaryTypographyProps={{
            color: "#222222",
            variant: "h3",
            fontWeight: "bold",
            textAlign: "center",
          }}
          secondary="〜うんちが出そう〜"
          secondaryTypographyProps={{
            color: "#22222",
            variant: "h6",
            textAlign: "center",
          }}
        />
        <div id="ribonl" />
      </Grid>
    </div>
  );
}

export default Title;
