import React from "react";
import { useInView } from "react-intersection-observer";
import "animate.css";
import {
  Typography,
  ListItem,
  ListItemButton,
  ListItemText,
  Grid,
} from "@mui/material";

import { format, parse } from "date-fns";
import { Box } from "@mui/system";

function formatDate(inputDate: any) {
  const formatday = format(
    parse(inputDate, "yyyyMMdd", new Date()),
    "yyyy年.MM.dd"
  );
  return formatday;
}
const informations = [
  [formatDate(20230312), "コーラックを飲みました。今日も快便です。"],
  [formatDate(20230311), "便秘になりました。"],
  [formatDate(20230310), "痔の手術しました。今日も快便です"],
  [formatDate(20230309), "痔になりました。お尻が痛みます。"],
  [formatDate(20231231), "今日も快便です"],
];
function WhatNew() {
  const { ref, inView } = useInView({
    // オプション
    rootMargin: "-50px", // ref要素が現れてから50px過ぎたら
    triggerOnce: true, // 最初の一度だけ実行
  });
  return (
    <Box className="WhatNew" ref={ref}>
      <Grid
        container
        direction="column"
        justifyContent="center"
        alignItems="center"
        mb={6}
        className="animate__animated animate__fadeInUp"
      >
        <Grid item xs={12} md={6}>
          {inView && (
            <Typography variant="h4" align="center" mb={3}>
              お知らせ
            </Typography>
          )}
        </Grid>
        <Grid item xs={12} md={6}>
          {informations.map((item: any, _index) => {
            return (
              <ListItem disablePadding key={_index}>
                <Grid item container flexDirection={"column"}>
                  <ListItemButton
                    sx={{
                      textAlign: "left",
                      borderBottom: 0.5,
                      borderColor: "#e6e6e6",
                    }}
                    disableRipple
                  >
                    <ListItemText
                      primary={item[0]}
                      primaryTypographyProps={{
                        color: "secondary",
                      }}
                    />

                    <Grid ml={4}>
                      <ListItemText
                        primary={item[1]}
                        primaryTypographyProps={{
                          color: "secondary",
                        }}
                      />
                    </Grid>
                  </ListItemButton>
                </Grid>
              </ListItem>
            );
          })}
        </Grid>
      </Grid>
    </Box>
  );
}

export default WhatNew;
