import React from "react";
import ImageDialog from "../dialog/ImageDialog";
import { Typography, Box, Grid, Card, CardMedia } from "@mui/material";
import SampleImage1 from "../dawnload/sampleImage_cat.jpg";
import SampleImage2 from "../dawnload/sampleImage_dog.jpg";
import { useInView } from "react-intersection-observer";
import "animate.css";
export interface bookParam {
  id: number;
  image: string;
}
const bookList = [
  {
    id: 1,
    image: SampleImage1,
  },
  {
    id: 2,
    image: SampleImage2,
  },
];
function Schedule() {
  const { ref, inView } = useInView({
    // オプション
    rootMargin: "-50px", // ref要素が現れてから50px過ぎたら
    triggerOnce: true, // 最初の一度だけ実行
  });
  return (
    <Box className="Schedule" id="item_5" ref={ref}>
      {inView && (
        <Box>
          <Grid
            container
            justifyContent="center"
            alignItems="center"
            margin="auto"
            spacing={3}
            mb={6}
            className="animate__animated animate__fadeInUp"
          >
            <Typography variant="h4" style={{ textAlign: "center" }} mb={3}>
              スケジュール
            </Typography>
            <Box>
              <Grid
                container
                margin="auto"
                spacing={3}
                mb={6}
                className="animate__animated animate__fadeInUp"
              >
                {bookList.map((item, index) => (
                  <Grid item key={index}>
                    <ImageDialog {...item} />
                  </Grid>
                ))}
              </Grid>
            </Box>
          </Grid>
        </Box>
      )}
    </Box>
  );
}

export default Schedule;
