import React, { useState } from "react";
import Typography from "@mui/material/Typography";
import { init, send } from "@emailjs/browser";
import { Grid, TextField, Button, Input } from "@mui/material";
import classNames from "classnames";
import { styled } from "@mui/material/styles";

const myStyleText = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
};
const myStyleButton = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
};

function Inquiry() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [content, setContent] = useState("");
  return (
    <div id="item_9" className="Inquiry" style={{ height: "70vh" }}>
      <form onSubmit={(e) => onSubmit(e)}>
        <Grid
          container
          direction="column"
          justifyContent="center"
          alignItems="center"
        >
          <Grid item mb={7}>
            <Typography variant="h4">お問い合わせ</Typography>
          </Grid>
          <Grid item container direction={"column"} alignItems="center" mb={3}>
            <Grid item md={6}>
              <TextField
                id="name"
                label="お名前"
                variant="outlined"
                size="small"
                onChange={(e) => setName(e.target.value)}
                fullWidth
                color="secondary"
                sx={myStyleText}
              />
            </Grid>
          </Grid>
          <Grid item xs={12} md={6} mb={3}>
            <TextField
              id="email"
              label="メールアドレス"
              variant="outlined"
              size="small"
              onChange={(e) => setEmail(e.target.value)}
              fullWidth
              color="secondary"
              sx={myStyleText}
            />
          </Grid>
          <Grid
            item
            container
            direction={"column"}
            alignItems="center"
            xs={12}
            md={6}
            mb={3}
          >
            <TextField
              id="content"
              label="お問い合わせ内容"
              multiline
              variant="outlined"
              size="medium"
              onChange={(e) => setContent(e.target.value)}
              fullWidth
              color="secondary"
              sx={myStyleText}
            />
          </Grid>
          <Grid
            item
            container
            direction={"column"}
            alignItems="flex-end"
            style={{ width: "30vw" }}
            mt={6}
          >
            <Button
              type="submit"
              variant="contained"
              size="large"
              sx={myStyleButton}
            >
              送信
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ flexGrow: 1 }}>
          <Grid
            container
            spacing={{ xs: 2, md: 3 }}
            columns={{ xs: 4, sm: 8, md: 12 }}
          >
            {Array.from(Array(6)).map((_, index) => (
              <Grid item xs={2} sm={4} md={4} key={index}>
                <Item>xs=2</Item>
              </Grid>
            ))}
          </Grid>
        </Box>
      </form>
    </div>
  );
}
const onSubmit = async (props: any) => {
  // 必要なIDをそれぞれ環境変数から取得
  const userID = process.env.REACT_APP_PUBLICKEY || "";
  const serviceID = process.env.REACT_APP_SERVICE_ID || "";
  const templateID = process.env.REACT_APP_TEMPLATE_ID || "";
  // emailJS初期化
  console.log("aaa");
  init(userID);

  // emailJS送信データを定義
  const params = {
    from_name: props.name,
    email: props.email,
    content: props.content,
  };

  // emailJS送信
  try {
    await send(serviceID, templateID, params, userID);
    alert("送信成功");
  } catch (error) {
    // 送信失敗したらalertで表示
    alert(error);
  }
};

export default Inquiry;
