import React from "react";
import { Typography, CardMedia, Grid } from "@mui/material";
import SampleImage1 from "../dawnload/sampleImage_cat.jpg";
function Advertisement() {
  const bookList = [
    {
      id: 1,
      image: SampleImage1,
    },
    {
      id: 2,
      image: "",
    },
  ];
  return (
    <div id="item_9" className="Advertisement" style={{ height: "50vh" }}>
      <Grid container flexDirection={"column"}>
        <Grid item xs={5}>
          <Typography variant="h4" style={{ textAlign: "center" }}>
            広告
          </Typography>
        </Grid>
        <Grid item xs={5}>
          <CardMedia
            component="img"
            image={SampleImage1}
            title={"広告"}
            style={{ height: "50vh" }}
          />
        </Grid>
      </Grid>
    </div>
  );
}

export default Advertisement;
