import React, { useState } from "react";
import { Grid, TextField, Button, Box, Typography } from "@mui/material";
import { useInView } from "react-intersection-observer";
import "animate.css";

const myStyleText = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  width: { sm: 200, md: 500 },
};
const myStyleMultiTextSmall = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "block", sm: "none" },
};
const myStyleMultiTextLarge = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "none", sm: "block" },
};
const myStyleBox = {
  width: { sm: 200, md: 500 },
};
const myStyleButtonSmall = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "block", sm: "none" },
};
const myStyleButtonLarge = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "none", sm: "block" },
};

function Inquiry() {
  interface MailObject {
    name: string;
    address: string;
    content: string;
  }
  //  メールフォーム
  const [mailForm, setMailForm] = useState<MailObject>({
    name: "",
    address: "",
    content: "",
  });
  //  エラーフォーム
  const [mailError, setMailError] = useState<MailObject>({
    name: "",
    address: "",
    content: "",
  });

  const { ref, inView } = useInView({
    // オプション
    rootMargin: "-50px", // ref要素が現れてから50px過ぎたら
    triggerOnce: true, // 最初の一度だけ実行
  });

  function mailCheck(props: any) {
    props.preventDefault();
    if (!mailForm.name) {
      setMailError({ ...mailError, name: "お名前を入力してください" });
    } else if (!mailForm.address) {
      setMailError({
        ...mailError,
        address: "メールアドレスを入力してください",
      });
    } else if (!mailForm.content) {
      setMailError({
        ...mailError,
        content: "お問い合わせ内容を入力してください",
      });
    } else {
      setMailError({ ...mailError, name: "", address: "", content: "" });
      onSubmit(mailForm);
    }
  }
  const setName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, name: e.target.value });
  };
  const setEmail = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, address: e.target.value });
  };
  const setContent = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, content: e.target.value });
  };
  return (
    <Box id="item_9" className="Inquiry" style={{ height: "50vh" }} ref={ref}>
      {inView && (
        <form onSubmit={(e) => mailCheck(e)}>
          <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
            className="animate__animated animate__fadeInUp"
          >
            <Grid item mb={7}>
              <Typography variant="h4">お問い合わせ</Typography>
            </Grid>
            <Box>
              <Grid item mb={3}>
                <TextField
                  id="name"
                  label="お名前"
                  variant="outlined"
                  size="small"
                  error={!!mailError.name}
                  helperText={mailError.name}
                  onChange={setName}
                  color="secondary"
                  sx={myStyleText}
                />
              </Grid>
              <Grid item mb={3}>
                <TextField
                  id="email"
                  label="メールアドレス"
                  variant="outlined"
                  size="small"
                  error={!!mailError.address}
                  helperText={mailError.address}
                  onChange={setEmail}
                  color="secondary"
                  sx={myStyleText}
                />
              </Grid>
              <Grid item mb={3}>
                <TextField
                  id="content"
                  label="お問い合わせ内容"
                  multiline
                  rows={4}
                  variant="outlined"
                  error={!!mailError.content}
                  helperText={mailError.content}
                  onChange={setContent}
                  color="secondary"
                  fullWidth
                  sx={myStyleMultiTextSmall}
                />

                <TextField
                  id="content"
                  label="お問い合わせ内容"
                  multiline
                  rows={4}
                  variant="outlined"
                  error={!!mailError.content}
                  helperText={mailError.content}
                  onChange={setContent}
                  color="secondary"
                  fullWidth
                  sx={myStyleMultiTextLarge}
                />
              </Grid>
              <Grid item>
                <Box
                  sx={myStyleBox}
                  display="flex"
                  alignItems="flex-end"
                  justifyContent="flex-end"
                >
                  <Button
                    type="submit"
                    variant="contained"
                    size="small"
                    sx={myStyleButtonSmall}
                  >
                    送信
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    size="large"
                    sx={myStyleButtonLarge}
                  >
                    送信
                  </Button>
                </Box>
              </Grid>
            </Box>
          </Grid>
        </form>
      )}
    </Box>
  );
}
const onSubmit = async (props: any) => {
  // 必要なIDをそれぞれ環境変数から取得
  sgMail.setApiKey(process.env.REACT_APP_SENDGRID_API_KEY as string);
  // 送信データを定義
  const params = {
    to: process.env.REACT_APP_MAIL_TO || "",
    from: props.address,
    subject: "時刻表アプリからの問い合わせ",
    //from_name: props.name,
    text: props.content,
  };

  // 送信
  try {
    await sgMail.send(params);
    alert("メールを送信しました。");
  } catch (error) {
    // 送信失敗したらalertで表示
    alert(error);
  }
};

export default Inquiry;
