import React from "react";
import {
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Grid,
} from "@mui/material";

import { format, parse } from "date-fns";

function formatDate(inputDate: any) {
  const formatday = format(
    parse(inputDate, "yyyyMMdd", new Date()),
    "yyyy年.MM.dd"
  );
  return formatday;
}
let informations = [
  [formatDate(20230312), "コーラックを飲みました。今日も快便です。"],
  [formatDate(20230311), "便秘になりました。"],
  [formatDate(20230310), "痔の手術しました。今日も快便です"],
  [formatDate(20230309), "痔になりました。お尻が痛みます。"],
  [formatDate(20231231), "今日も快便です"],
];
function WhatNew() {
  return (
    <div
      className="WhatNew"
      style={{
        width: "50vw",
      }}
    >
      <Grid alignItems="center" justify="center">
        <Typography style={{ color: "blue" }}>お知らせ</Typography>
        <div>
          {informations.map((item: any, _index) => {
            return (
              <List>
                <ListItem disablePadding>
                  <ListItemButton>
                    {item.map((subItems: any, subIndex: number) => {
                      return <ListItemText primary={subItems} />;
                    })}
                  </ListItemButton>
                </ListItem>
              </List>
            );
          })}
        </div>
      </Grid>
    </div>
  );
}

export default WhatNew;
