import React from "react";
import YouTube, { YouTubeProps } from "react-youtube";
import { Grid, Typography, Link } from "@mui/material";

function IntroVideo() {
  const myStyleBox = {
    //width: { sm: 200, md: 500 },
  };
  const opts: YouTubeProps["opts"] = {
    position: "relative",
    height: "100",
    width: "640",
    playerVars: {
      autoplay: 1,
    },
  };
  return (
    <div className="IntroVideo" id="item_1">
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
        mb={6}
      >
        <Grid item>
          <Typography variant="h4" mb={3}>
            紹介ビデオ
          </Typography>
        </Grid>
        <Grid item>
          <YouTube
            videoId="lTRiuFIWV54"
            opts={opts}
            style={{
              position: "relative",
              width: "50%",
            }}
          />
        </Grid>
        {/* <Grid
          item
          sx={{
            display: { xs: "block", sm: "none" },
          }}
        >
          <Link>こちらをクリック</Link>
        </Grid> */}
      </Grid>
    </div>
  );
}

export default IntroVideo;
