import React from "react";
import YouTube, { YouTubeProps } from "react-youtube";
import tubeStyle from "../css/Youtube.module.css";
import { Grid, Typography, Link } from "@mui/material";

function IntroVideo() {
  const smallOpts: YouTubeProps["opts"] = {
    height: "195",
    width: "320",
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };
  const largeOpts: YouTubeProps["opts"] = {
    height: "390",
    width: "640",
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };
  const myStyleYoutubeSmall = {
    display: { xs: "none", sm: "block" },
  };
  const myStyleYoutubeLarge = {
    display: { xs: "block", sm: "none" },
  };
  return (
    <div className="IntroVideo" id="item_1">
      <Grid
        container
        justifyContent="center"
        alignItems="center"
        flexDirection="column"
        mb={6}
      >
        <Grid item>
          <Typography variant="h4" mb={3}>
            紹介ビデオ
          </Typography>
        </Grid>
        <Grid item>
          <YouTube
            videoId="lTRiuFIWV54"
            opts={smallOpts}
            className={myStyleYoutubeSmall}
          />
        </Grid>
      </Grid>
    </div>
  );
}

export default IntroVideo;
