import React from "react";
import {
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
} from "@mui/material";

import { format, parse } from "date-fns";

function formatDate(inputDate: any, info: string) {
  const formatday = format(
    parse(inputDate, "yyyyMMdd", new Date()),
    "yyyy年.MM.dd"
  );
  return { formatday, info };
}
const informations = [
  formatDate(20230312, "コーラックを飲みました。今日も快便です。"),
  formatDate(20230311, "便秘になりました。"),
  formatDate(20230310, "痔の手術しました。今日も快便です"),
  formatDate(20230309, "痔になりました。お尻が痛みます。"),
  formatDate(20231231, "今日も快便です"),
];
function WhatNew() {
  return (
    <div className="WhatNew">
      <Typography style={{ color: "red" }}>お知らせ</Typography>
      <div>
        {informations.map((item: any, index: number) => {
          return <Typography key={index}>{item}</Typography>;
        })}
      </div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Dessert (100g serving)</TableCell>
              <TableCell align="right">Calories</TableCell>
              <TableCell align="right">Fat&nbsp;(g)</TableCell>
              <TableCell align="right">Carbs&nbsp;(g)</TableCell>
              <TableCell align="right">Protein&nbsp;(g)</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow
                key={row.name}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="right">{row.calories}</TableCell>
                <TableCell align="right">{row.fat}</TableCell>
                <TableCell align="right">{row.carbs}</TableCell>
                <TableCell align="right">{row.protein}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}

export default WhatNew;
