import React, { useState, useRef } from "react";
import Typography from "@mui/material/Typography";
import { init, send } from "@emailjs/browser";
import { Grid, TextField, Button } from "@mui/material";
import { Box } from "@mui/system";
import { useInView } from "react-intersection-observer";
import "animate.css";

const myStyleText = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  width: { sm: 200, md: 500 },
};
const myStyleMultiTextSmall = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "block", sm: "none" },
};
const myStyleMultiTextLarge = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "none", sm: "block" },
};
const myStyleBox = {
  width: { sm: 200, md: 500 },
};
const myStyleButtonSmall = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "block", sm: "none" },
};
const myStyleButtonLarge = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "none", sm: "block" },
};

function Inquiry() {
  interface MailObject {
    name: string;
    address: string;
    content: string;
  }
  //  メールフォーム
  const [mailForm, setMailForm] = useState<MailObject>({
    name: "",
    address: "",
    content: "",
  });
  interface MailErrorObject {
    nameError: string;
    addressError: string;
    contentError: string;
  }
  //  エラーフォーム
  const [mailError, setMailError] = useState<MailErrorObject>({
    nameError: "",
    addressError: "",
    contentError: "",
  });

  const { ref, inView } = useInView({
    // オプション
    rootMargin: "-50px", // ref要素が現れてから50px過ぎたら
    triggerOnce: true, // 最初の一度だけ実行
  });

  function mailCheck(props: any) {
    props.preventDefault();
    if (!mailForm.name) {
      props.setEmailErrorName("お名前を入力してください");
    } else if (!mailForm.address) {
      props.setEmailErrorAddress("メールアドレスを入力してください");
    } else if (!mailForm.content) {
      props.setEmailErrorContent("お問い合わせ内容を入力してください");
    } else {
      onSubmit(props);
    }
  }
  const setName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, name: e.target.value });
  };
  const setEmail = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, address: e.target.value });
  };
  const setContent = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, content: e.target.value });
  };
  const setErrorName = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, name: e.target.value });
  };
  const setErrorEmail = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, address: e.target.value });
  };
  const setErrorContent = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMailForm({ ...mailForm, content: e.target.value });
  };
  return (
    <Box id="item_9" className="Inquiry" style={{ height: "50vh" }} ref={ref}>
      {inView && (
        <form onSubmit={(e) => mailCheck(e)}>
          <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
            className="animate__animated animate__fadeInUp"
          >
            <Grid item mb={7}>
              <Typography variant="h4">お問い合わせ</Typography>
            </Grid>
            <Box>
              <Grid item mb={3}>
                <TextField
                  id="name"
                  label="お名前"
                  variant="outlined"
                  size="small"
                  error={!!setErrorName}
                  helperText={nameError}
                  onChange={setName}
                  color="secondary"
                  sx={myStyleText}
                />
              </Grid>
              <Grid item mb={3}>
                <TextField
                  id="email"
                  label="メールアドレス"
                  variant="outlined"
                  size="small"
                  error={!!emailErrorAddress}
                  helperText={emailErrorAddress}
                  onChange={setEmail}
                  color="secondary"
                  sx={myStyleText}
                />
              </Grid>
              <Grid item mb={3}>
                <TextField
                  id="content"
                  label="お問い合わせ内容"
                  multiline
                  rows={4}
                  variant="outlined"
                  error={!!emailErrorContent}
                  helperText={emailErrorContent}
                  onChange={setContent}
                  color="secondary"
                  fullWidth
                  sx={myStyleMultiTextSmall}
                />

                <TextField
                  id="content"
                  label="お問い合わせ内容"
                  multiline
                  rows={4}
                  variant="outlined"
                  error={!!emailErrorContent}
                  helperText={emailErrorContent}
                  onChange={(e) => setContent(e.target.value)}
                  color="secondary"
                  fullWidth
                  sx={myStyleMultiTextLarge}
                />
              </Grid>
              <Grid item>
                <Box
                  sx={myStyleBox}
                  display="flex"
                  alignItems="flex-end"
                  justifyContent="flex-end"
                >
                  <Button
                    type="submit"
                    variant="contained"
                    size="small"
                    sx={myStyleButtonSmall}
                  >
                    送信
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    size="large"
                    sx={myStyleButtonLarge}
                  >
                    送信
                  </Button>
                </Box>
              </Grid>
            </Box>
          </Grid>
        </form>
      )}
    </Box>
  );
}
const onSubmit = async (props: any) => {
  props.preventDefault();

  // 必要なIDをそれぞれ環境変数から取得
  const userID = process.env.REACT_APP_PUBLICKEY || "";
  const serviceID = process.env.REACT_APP_SERVICE_ID || "";
  const templateID = process.env.REACT_APP_TEMPLATE_ID || "";
  // emailJS初期化
  init(userID);

  // emailJS送信データを定義
  const params = {
    formName: props.name,
    email: props.email,
    content: props.content,
  };

  // emailJS送信
  try {
    await send(serviceID, templateID, params, userID);
    alert("メールを送信しました。");
  } catch (error) {
    // 送信失敗したらalertで表示
    alert(error);
  }
};

export default Inquiry;
