    },
    {
      name: "うまるちゃん",
      image: SampleImage1,
      url: "http://banner.keizine.net/archives/7760/",
    },
  ];
  return (
    <Box sx={myStyleBox} justifyContent="center" ref={ref}>
      <Grid
        id="item_10"
        className="Advertisement"
        container
        flexDirection={"column"}
        alignItems="center"
      >
        <Grid item>
          <Typography variant="h4" mb={3}>
            広告
          </Typography>
        </Grid>
        <Grid
          item
          container
          flexDirection={"row"}
          //spacing={{ xs: 4, sm: 8, md: 1 }}
          justifyContent="space-around"
          alignItems="center"
          xs={4}
          sm={6}
          md={6}
          gridTemplateColumns="repeat(auto-fit, minmax(200px, 1fr))"
          gap=" 24px 6%"
        >
          {adList.map((_item, _index) => (
            <Grid item xs={4} md={3} key={_index}>
              <Link href={_item.url}>
                <CardMedia
                  component="img"
                  image={_item.image}
                  title={"広告" + _index}
                />
              </Link>
            </Grid>
          ))}
        </Grid>
      </Grid>
    </Box>
  );
}

export default Advertisement;
