import * as React from "react";

import Dialog from "@mui/material/Dialog";

export interface SimpleDialogProps {
  open: boolean;
  imageValue: string;
  onClose: () => void;
}

function SimpleDialog(props: SimpleDialogProps) {
  const handleClose = () => {
    props.onClose();
  };

  return (
    <Dialog onClose={handleClose} open={props.open}>
      {
        <CardMedia
          component="img"
          image={props.imageValue}
          title={"スケジュール"}
          onClick={handleClose}
        />
      }
    </Dialog>
  );
}

export default function ImageDialog(props: any) {
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <CardMedia
        component="img"
        image={props.bookParam.image}
        title={"スケジュール" + props.bookParam.id}
        style={{ maxWidth: props.bookParam.sizePer + "%" }}
        onClick={handleClickOpen}
      />
      <SimpleDialog
        imageValue={props.bookParam.image}
        open={open}
        onClose={handleClose}
      />
    </div>
  );
}
