import React, { useState } from "react";
import {
  Grid,
  TextField,
  Button,
  Box,
  Typography,
  Link,
  ListItemText,
} from "@mui/material";
import { useInView } from "react-intersection-observer";
import "animate.css";

const myStyleText = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  width: { sm: 200, md: 500 },
};
const myStyleMultiTextSmall = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "block", sm: "none" },
};
const myStyleMultiTextLarge = {
  "& .MuiInputBase-input": {
    color: "#6f5b41", // 入力文字の色
  },
  display: { xs: "none", sm: "block" },
};
const myStyleBox = {
  width: { sm: 200, md: 500 },
};
const myStyleButtonSmall = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "block", sm: "none" },
};
const myStyleButtonLarge = {
  "&:hover": {
    backgroundColor: "#fbae49",
  },
  display: { xs: "none", sm: "block" },
};

function Inquiry() {
  const { ref, inView } = useInView({
    // オプション
    rootMargin: "-50px", // ref要素が現れてから50px過ぎたら
    triggerOnce: true, // 最初の一度だけ実行
  });

  return (
    <Box id="item_9" className="Inquiry" style={{ height: "50vh" }} ref={ref}>
      {inView && (
        <Grid
          container
          flexDirection="column"
          textAlign="center"
          alignItems="center"
          sx={{ mx: "auto" }}
          mb={12}
          className="animate__animated animate__fadeInUp"
        >
          <Grid item mb={"4vh"}>
            <ListItemText
              primary="お問い合わせ"
              primaryTypographyProps={{
                variant: "h4",
              }}
              secondary="お手数ですが以下のメールアドレスからお送りください"
              secondaryTypographyProps={{
                color: "secondary",
                variant: "h6",
              }}
            />
          </Grid>
          <Grid
            item
            mb={"8vh"}
            container
            direction={"column"}
            alignItems="canter" //左寄せよせの場合はflex-start
          >
            <Typography variant="h6" color="secondary">
              ***@gmail.com
            </Typography>
          </Grid>
          <Grid item>
            <Grid container>
              <Grid item mr={"5vw"}>
                <Button variant="contained" size="large">
                  <Link href="src\dawnload\img.png" download="img.png">
                    <Typography style={{ color: "white" }}>
                      ダウンロード
                    </Typography>
                  </Link>
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      )}
    </Box>
  );
}

export default Inquiry;
